mapper.insert:
 		insert into t_with_Member(member_name, id, password, member_type, member_gender, member_age)
		values(
		 #{memberName},
		 #{id},
		 #{password},
		 #{memberType},
		 #{memberGender},
		 #{memberAge}
		 	);

mapper.select:
select count(*)
from t_with_Member
where id = #{id}
