mapper.select:
	select 
		IFNULL(SUM(M_MONEY), 0) as mMoney
	from 
		T_WITH_STUDYGROUP_MONEYBOOK 
	where
		group_no = #{groupNo}
	and
		date_format(M_DATE, '%Y-%m-%d') between #{startDate} and #{endDate}
  and
    m_right = #{search}

		select 
			IFNULL(SUM(M_MONEY), 0) as money
		from 
			T_WITH_STUDYGROUP_MONEYBOOK 
		where
			group_no = #{groupNo}
		and
			date_format(M_DATE, '%Y-%m-%d') between #{startDate} and #{endDate}
	  and
	    m_left = #{category}
	  and
	  	m_right = #{search}
